import React, { Fragment, useEffect } from "react";
import { DataGrid } from "@material-ui/data-grid";
import "./ProductReviews.css";
import { useSelector, useDispatch } from "react-redux";
import {clearErrors, getAllReviews, deleteReviews} from "../../actions/productAction";  
import { useAlert } from "react-alert";
import { Button } from "@material-ui/core";
import MetaData from "../layout/MetaData";
import DeleteIcon from "@material-ui/icons/Delete";
import Star from "@material-ui/icons/Star";
import Sidebar from "./Sidebar";
import { DELETE_REVIEW_RESET } from "../../constants/productConstants";
import { useNavigate } from "react-router-dom";
const ProductReviews = () => {

    const dispatch = useDispatch();
    const navigate = useNavigate();
    
    const alert = useAlert();

    const { error: deleteError, isDeleted } = useSelector(
        (state) => state.review);

    const {error, reviews, loading} = useSelector((state) => state.productReviews);

    const [productId, setProductId] = useState("");    

    const deleteProductHandler =(id) =>{
       // dispatch(deleteProduct(id));
    }

    const productReviewsSubmitHandler = () => {};

    useEffect(() => {
        if (error) {
            alert.error(error);
            dispatch(clearErrors());
        }

        if (deleteError) {
        alert.error(deleteError);
        dispatch(clearErrors());
        }

        if (isDeleted) {
            alert.success("Reviews Deleted Successfully");
            navigate("/admin/dashboard");
            dispatch({ type: DELETE_REVIEW_RESET });
        }

    }, [dispatch, alert, error, deleteError, navigate, isDeleted ]);

    const columns = [
        { field: "id", headerName: "Review ID", minWidth: 150, flex:0.5 },

        {
            field: "name",
            headerName : "Name",
            minWidth: 150,
            flex: 0.3,
        },
        {
            field: "comment",
            headerName : "Comment",
            minWidth: 100,
            flex: 1,
        },
        {
            field: "rating",
            headerName : "Rating",
            type:"number",
            minWidth: 150,
            flex: 0.5,

            cellClassName: (params) => {
                return params.getValue(params.id, "rating") >= 3
                ? "greenColor"
                : "redColor";
            },
        },
        {
            field: "actions",
            flex: 0.3,
            headerName: "Actions",
            minWidth: 150,
            type: "number",
            sortable: false,
            renderCall: (params) =>{
                return (
                    <Fragment>
                   <Button 
                   onClick={()=>
                   deleteProductHandler(params.getValue(params.id, "id"))
                   }>
                    <DeleteIcon />
                   </Button>
                    </Fragment>
                )
            }
        }
    ];

   const rows = [];

   reviews &&
     reviews.forEach((item) => {
        rows.push({
            id: item._id,
            rating: item.rating,
            comment: item.comment,
            name: item.user,
        });
     });

    return (<Fragment>
        <MetaData title={`ALL REVIEWS -Admin`} />
            
        <div className="dashboard">
        <Sidebar />
        <div className="productListContainer">

        <form
           className="createProductForm"
           encType="multipart/form-data"
           onSubmit={productReviewsSubmitHandler}
           >
           <h1>ALL REVIEWS</h1>

           <div>
            <Star />
            <input
                type="text"
                placeholder="Name"
                required
                value={productId}
                onChange={(e) => setProductId(e.target.value)}
            />
           </div>

           <Button
             id="createProductBtn"
             type="submit"
             disabled={loading ? true: false || productId ==="" ? true : false}
           >
           Update 
           </Button>
           </form>

      {reviews && reviews.l}

        </div>
        </div>
    </Fragment>
    );
};

export default ProductReviews;
