import React, { useRef, useState } from "react";
import "./LoginSignUp.css";
import Loader from "../layout/Loader/Loader";
import { Link, Routes, Route, Outlet } from "react-router-dom";
import MailOutlineIcon from "@material-ui/icons/MailOutline";
import LockOpenIcon from "@material-ui/icons/LockOpen";
import FaceIcon from "@material-ui/icons/Face";

const Login = ({ loginSubmit, loginEmail, setLoginEmail, loginPassword, setLoginPassword }) => {
  return (
    <form className="loginForm" onSubmit={loginSubmit}>
      {/* Your login form JSX */}
    </form>
  );
};

const SignUp = ({ registerSubmit, name, email, password, avatarPreview, registerDataChange }) => {
  return (
    <form className="signUpForm" encType="multipart/form-data" onSubmit={registerSubmit}>
      {/* Your sign up form JSX */}
    </form>
  );
};

const LoginSignUp = () => {
  // Your existing code for state and functions

  return (
    <>
      <div className="LoginSignUpContainer">
        <div className="LoginSignUpBox">
          <div>
            <div className="login_signUp_toggle">
              <Link to="/login">LOGIN</Link>
              <Link to="/register">REGISTER</Link>
            </div>
            <button ref={switcherTab}></button>
          </div>

          <Routes>
            <Route path="/login" element={<Login loginSubmit={loginSubmit} loginEmail={loginEmail} setLoginEmail={setLoginEmail} loginPassword={loginPassword} setLoginPassword={setLoginPassword} />} />
            <Route
              path="/register"
              element={
                <SignUp
                  registerSubmit={registerSubmit}
                  name={name}
                  email={email}
                  password={password}
                  avatarPreview={avatarPreview}
                  registerDataChange={registerDataChange}
                />
              }
            />
          </Routes>
        </div>
      </div>
    </>
  );
};

export default LoginSignUp;
