import {
    ALL_PRODUCT_REQUEST,
    ALL_PRODUCT_SUCCESS,
    ALL_PRODUCT_FAIL,
    PRODUCT_DETAILS_REQUEST,
    PRODUCT_DETAILS_FAIL,
    PRODUCT_DETAILS_SUCCESS,
    NEW_REVIEW_REQUEST,
    NEW_REVIEW_SUCCESS,
    NEW_REVIEW_RESET,
    NEW_REVIEW_FAIL,
    CLEAR_ERRORS,
} from "../constants/productConstants";

export const productReducer = 
(state = {loading: false, products: [] }, action) => { 
    switch (action.type) {
        case ALL_PRODUCT_REQUEST:    
        return {
            loading:true,
            products: [],
        };

        case ALL_PRODUCT_SUCCESS:   
        return {
            loading:false,
            products:action.payload.products,
            productsCount: action.payload.productsCount,
            resultPerPage:action.payload.resultPerPage,
            filteredProductsCount:action.payload.filteredProductsCount,
            //error: null,
        };

        case ALL_PRODUCT_FAIL: 
        return {
            loading:false,
            error:action.payload,
        };
         
        case CLEAR_ERRORS: 
        return {
            ...state,
            error: null,
        };

        default:
            return state;
    }    
};

export const productDetailsReducer = (
    state = {product: {} },action) => {
           
   
    switch (action.type) {

        case PRODUCT_DETAILS_REQUEST:    
        return {
            loading:true,
            ...state,
           
        };

        case PRODUCT_DETAILS_SUCCESS:   
        return {
            loading:false,
            product: action.payload,
        };

        case PRODUCT_DETAILS_FAIL: 
        return {
            loading:false,
            error:action.payload,
        };
         
        case CLEAR_ERRORS: 
        return {
            ...state,
            error: null,
        };

        default:
            return state;
    }    
};

export const prodReducer = (
    state = {product: {} },action) => {
           
   
    switch (action.type) {

        case PRODUCT_DETAILS_REQUEST:    
        return {
            loading:true,
            ...state,
           
        };

        case PRODUCT_DETAILS_SUCCESS:   
        return {
            loading:false,
            product: action.payload,
        };

        case PRODUCT_DETAILS_FAIL: 
        return {
            loading:false,
            error:action.payload,
        };
         
        case CLEAR_ERRORS: 
        return {
            ...state,
            error: null,
        };

        default:
            return state;
    }    
};
